// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title AccessControl
 * @dev Contract module that allows children to implement role-based access control mechanisms.
 * Roles: Owner, Admin, Finance Manager, Employee
 */
contract AccessControl {
    // Role definitions
    bytes32 public constant OWNER_ROLE = keccak256("OWNER_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant FINANCE_MANAGER_ROLE = keccak256("FINANCE_MANAGER_ROLE");
    bytes32 public constant EMPLOYEE_ROLE = keccak256("EMPLOYEE_ROLE");

    // Role mappings
    mapping(bytes32 => mapping(address => bool)) private _roles;
    mapping(bytes32 => address[]) private _roleMembers;
    
    // Owner address
    address private _owner;

    // Events
    event RoleGranted(bytes32 indexed role, address indexed account, address indexed sender);
    event RoleRevoked(bytes32 indexed role, address indexed account, address indexed sender);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        _owner = msg.sender;
        _grantRole(OWNER_ROLE, msg.sender);
    }

    /**
     * @dev Modifier that checks if the caller has a specific role.
     */
    modifier onlyRole(bytes32 role) {
        require(hasRole(role, msg.sender), "AccessControl: sender does not have required role");
        _;
    }

    /**
     * @dev Modifier that checks if the caller is the owner.
     */
    modifier onlyOwner() {
        require(msg.sender == _owner, "AccessControl: caller is not the owner");
        _;
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Returns `true` if `account` has been granted `role`.
     */
    function hasRole(bytes32 role, address account) public view returns (bool) {
        return _roles[role][account];
    }

    /**
     * @dev Returns all members of a specific role.
     */
    function getRoleMembers(bytes32 role) public view returns (address[] memory) {
        return _roleMembers[role];
    }

    /**
     * @dev Grants `role` to `account`.
     * Can only be called by owner or admin.
     */
    function grantRole(bytes32 role, address account) public virtual {
        require(
            hasRole(OWNER_ROLE, msg.sender) || hasRole(ADMIN_ROLE, msg.sender),
            "AccessControl: must be owner or admin to grant roles"
        );
        require(account != address(0), "AccessControl: cannot grant role to zero address");
        _grantRole(role, account);
    }

    /**
     * @dev Revokes `role` from `account`.
     * Can only be called by owner or admin.
     */
    function revokeRole(bytes32 role, address account) public virtual {
        require(
            hasRole(OWNER_ROLE, msg.sender) || hasRole(ADMIN_ROLE, msg.sender),
            "AccessControl: must be owner or admin to revoke roles"
        );
        _revokeRole(role, account);
    }

    /**
     * @dev Renounces `role` from the calling account.
     */
    function renounceRole(bytes32 role) public virtual {
        _revokeRole(role, msg.sender);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "AccessControl: new owner is the zero address");
        address oldOwner = _owner;
        _owner = newOwner;
        _revokeRole(OWNER_ROLE, oldOwner);
        _grantRole(OWNER_ROLE, newOwner);
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    /**
     * @dev Internal function to grant a role to an account.
     */
    function _grantRole(bytes32 role, address account) internal {
        if (!_roles[role][account]) {
            _roles[role][account] = true;
            _roleMembers[role].push(account);
            emit RoleGranted(role, account, msg.sender);
        }
    }

    /**
     * @dev Internal function to revoke a role from an account.
     */
    function _revokeRole(bytes32 role, address account) internal {
        if (_roles[role][account]) {
            _roles[role][account] = false;
            
            // Remove from role members array
            address[] storage members = _roleMembers[role];
            for (uint256 i = 0; i < members.length; i++) {
                if (members[i] == account) {
                    members[i] = members[members.length - 1];
                    members.pop();
                    break;
                }
            }
            
            emit RoleRevoked(role, account, msg.sender);
        }
    }
}

