// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./AccessControl.sol";
import "./EmployeeRegistry.sol";
import "./InvoiceManager.sol";
import "./PaymentApproval.sol";
import "./interfaces/IERC20.sol";

/**
 * @title CorePayroll
 * @dev Main payroll management contract with batch payment capabilities
 * Supports multiple ERC20 tokens and native ETH payments
 */
contract CorePayroll is AccessControl {
    // Enums
    enum PaymentStatus { Pending, Processing, Completed, Failed }

    // Structs
    struct Payment {
        uint256 paymentId;
        uint256 employeeId;
        address employeeWallet;
        uint256 amount;
        address token; // address(0) for ETH
        PaymentStatus status;
        uint256 createdAt;
        uint256 completedAt;
        bytes32 transactionHash;
        string failureReason;
    }

    struct BatchPayment {
        uint256 batchId;
        uint256[] paymentIds;
        uint256 totalAmount;
        address token;
        PaymentStatus status;
        uint256 createdAt;
        uint256 completedAt;
        uint256 successCount;
        uint256 failureCount;
    }

    // State variables
    EmployeeRegistry public employeeRegistry;
    InvoiceManager public invoiceManager;
    PaymentApproval public paymentApproval;

    uint256 private _paymentIdCounter;
    uint256 private _batchIdCounter;
    
    bool public paused;

    // Mappings
    mapping(uint256 => Payment) private _payments;
    mapping(uint256 => BatchPayment) private _batchPayments;
    mapping(address => bool) public supportedTokens;
    mapping(uint256 => uint256) private _employeeLastPayment; // employeeId => timestamp
    
    uint256[] private _allPaymentIds;
    uint256[] private _allBatchIds;

    // Events
    event PaymentCreated(
        uint256 indexed paymentId,
        uint256 indexed employeeId,
        address indexed employeeWallet,
        uint256 amount,
        address token
    );

    event PaymentCompleted(
        uint256 indexed paymentId,
        uint256 indexed employeeId,
        bytes32 transactionHash
    );

    event PaymentFailed(
        uint256 indexed paymentId,
        uint256 indexed employeeId,
        string reason
    );

    event BatchPaymentCreated(
        uint256 indexed batchId,
        uint256 paymentCount,
        uint256 totalAmount,
        address token
    );

    event BatchPaymentCompleted(
        uint256 indexed batchId,
        uint256 successCount,
        uint256 failureCount
    );

    event TokenAdded(address indexed token);
    event TokenRemoved(address indexed token);
    event ContractPaused(address indexed by);
    event ContractUnpaused(address indexed by);
    event FundsDeposited(address indexed from, uint256 amount, address token);
    event FundsWithdrawn(address indexed to, uint256 amount, address token);

    /**
     * @dev Constructor
     */
    constructor(
        address _employeeRegistry,
        address _invoiceManager,
        address _paymentApproval
    ) AccessControl() {
        require(_employeeRegistry != address(0), "CorePayroll: invalid employee registry address");
        require(_invoiceManager != address(0), "CorePayroll: invalid invoice manager address");
        require(_paymentApproval != address(0), "CorePayroll: invalid payment approval address");

        employeeRegistry = EmployeeRegistry(_employeeRegistry);
        invoiceManager = InvoiceManager(_invoiceManager);
        paymentApproval = PaymentApproval(_paymentApproval);

        _paymentIdCounter = 1;
        _batchIdCounter = 1;
        paused = false;

        // ETH is supported by default
        supportedTokens[address(0)] = true;
    }

    /**
     * @dev Modifier to check if contract is not paused
     */
    modifier whenNotPaused() {
        require(!paused, "CorePayroll: contract is paused");
        _;
    }

    /**
     * @dev Modifier to check if contract is paused
     */
    modifier whenPaused() {
        require(paused, "CorePayroll: contract is not paused");
        _;
    }

    /**
     * @dev Pause the contract - emergency stop
     */
    function pause() external onlyRole(OWNER_ROLE) whenNotPaused {
        paused = true;
        emit ContractPaused(msg.sender);
    }

    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyRole(OWNER_ROLE) whenPaused {
        paused = false;
        emit ContractUnpaused(msg.sender);
    }

    /**
     * @dev Add a supported token
     */
    function addSupportedToken(address token) external onlyRole(ADMIN_ROLE) {
        require(token != address(0), "CorePayroll: invalid token address");
        require(!supportedTokens[token], "CorePayroll: token already supported");
        
        supportedTokens[token] = true;
        emit TokenAdded(token);
    }

    /**
     * @dev Remove a supported token
     */
    function removeSupportedToken(address token) external onlyRole(ADMIN_ROLE) {
        require(supportedTokens[token], "CorePayroll: token not supported");
        
        supportedTokens[token] = false;
        emit TokenRemoved(token);
    }

    /**
     * @dev Process a single salary payment
     */
    function processSalaryPayment(uint256 employeeId) 
        external 
        onlyRole(FINANCE_MANAGER_ROLE) 
        whenNotPaused 
        returns (uint256) 
    {
        require(employeeRegistry.employeeExists(employeeId), "CorePayroll: employee does not exist");

        EmployeeRegistry.Employee memory employee = employeeRegistry.getEmployee(employeeId);
        
        require(
            employee.status == EmployeeRegistry.EmployeeStatus.Active,
            "CorePayroll: employee is not active"
        );
        require(supportedTokens[employee.salaryToken], "CorePayroll: token not supported");

        // Create payment
        uint256 paymentId = _createPayment(
            employeeId,
            employee.walletAddress,
            employee.salaryAmount,
            employee.salaryToken
        );

        // Execute payment
        _executePayment(paymentId);

        return paymentId;
    }

    /**
     * @dev Process batch salary payments
     * Gas-optimized for multiple employees
     */
    function processBatchSalaryPayments(uint256[] calldata employeeIds) 
        external 
        onlyRole(FINANCE_MANAGER_ROLE) 
        whenNotPaused 
        returns (uint256) 
    {
        require(employeeIds.length > 0, "CorePayroll: no employees specified");

        uint256 batchId = _batchIdCounter++;
        uint256[] memory paymentIds = new uint256[](employeeIds.length);
        
        // Group payments by token for gas optimization
        for (uint256 i = 0; i < employeeIds.length; i++) {
            require(employeeRegistry.employeeExists(employeeIds[i]), "CorePayroll: employee does not exist");
            
            EmployeeRegistry.Employee memory employee = employeeRegistry.getEmployee(employeeIds[i]);
            
            require(
                employee.status == EmployeeRegistry.EmployeeStatus.Active,
                "CorePayroll: employee is not active"
            );
            require(supportedTokens[employee.salaryToken], "CorePayroll: token not supported");

            // Create payment
            uint256 paymentId = _createPayment(
                employeeIds[i],
                employee.walletAddress,
                employee.salaryAmount,
                employee.salaryToken
            );
            
            paymentIds[i] = paymentId;
        }

        // Create batch record
        BatchPayment memory batch = BatchPayment({
            batchId: batchId,
            paymentIds: paymentIds,
            totalAmount: 0,
            token: address(0), // Will be calculated per token
            status: PaymentStatus.Processing,
            createdAt: block.timestamp,
            completedAt: 0,
            successCount: 0,
            failureCount: 0
        });

        _batchPayments[batchId] = batch;
        _allBatchIds.push(batchId);

        emit BatchPaymentCreated(batchId, employeeIds.length, 0, address(0));

        // Execute batch payments
        _executeBatchPayments(batchId);

        return batchId;
    }

    /**
     * @dev Process payment from approved request
     */
    function processApprovedPayment(uint256 requestId) 
        external 
        onlyRole(FINANCE_MANAGER_ROLE) 
        whenNotPaused 
        returns (uint256) 
    {
        PaymentApproval.PaymentRequest memory request = paymentApproval.getPaymentRequest(requestId);
        
        require(
            request.status == PaymentApproval.ApprovalStatus.Approved,
            "CorePayroll: request not approved"
        );
        require(supportedTokens[request.token], "CorePayroll: token not supported");

        uint256 batchId = _batchIdCounter++;
        uint256[] memory paymentIds = new uint256[](request.employeeIds.length);
        uint256 totalAmount = 0;

        // Create payments
        for (uint256 i = 0; i < request.employeeIds.length; i++) {
            uint256 paymentId = _createPayment(
                request.employeeIds[i],
                request.employeeWallets[i],
                request.amounts[i],
                request.token
            );
            
            paymentIds[i] = paymentId;
            totalAmount += request.amounts[i];
        }

        // Create batch record
        BatchPayment memory batch = BatchPayment({
            batchId: batchId,
            paymentIds: paymentIds,
            totalAmount: totalAmount,
            token: request.token,
            status: PaymentStatus.Processing,
            createdAt: block.timestamp,
            completedAt: 0,
            successCount: 0,
            failureCount: 0
        });

        _batchPayments[batchId] = batch;
        _allBatchIds.push(batchId);

        emit BatchPaymentCreated(batchId, request.employeeIds.length, totalAmount, request.token);

        // Execute batch payments
        _executeBatchPayments(batchId);

        // Mark request as executed
        paymentApproval.markAsExecuted(requestId);

        return batchId;
    }

    /**
     * @dev Deposit funds to contract (for ETH)
     */
    function depositETH() external payable onlyRole(FINANCE_MANAGER_ROLE) {
        require(msg.value > 0, "CorePayroll: must send ETH");
        emit FundsDeposited(msg.sender, msg.value, address(0));
    }

    /**
     * @dev Deposit ERC20 tokens to contract
     */
    function depositTokens(address token, uint256 amount) 
        external 
        onlyRole(FINANCE_MANAGER_ROLE) 
    {
        require(supportedTokens[token], "CorePayroll: token not supported");
        require(amount > 0, "CorePayroll: amount must be greater than zero");

        IERC20(token).transferFrom(msg.sender, address(this), amount);
        
        emit FundsDeposited(msg.sender, amount, token);
    }

    /**
     * @dev Withdraw ETH from contract
     */
    function withdrawETH(uint256 amount) external onlyRole(OWNER_ROLE) {
        require(amount > 0, "CorePayroll: amount must be greater than zero");
        require(address(this).balance >= amount, "CorePayroll: insufficient balance");

        payable(msg.sender).transfer(amount);
        
        emit FundsWithdrawn(msg.sender, amount, address(0));
    }

    /**
     * @dev Withdraw ERC20 tokens from contract
     */
    function withdrawTokens(address token, uint256 amount) external onlyRole(OWNER_ROLE) {
        require(amount > 0, "CorePayroll: amount must be greater than zero");
        require(
            IERC20(token).balanceOf(address(this)) >= amount,
            "CorePayroll: insufficient token balance"
        );

        IERC20(token).transfer(msg.sender, amount);
        
        emit FundsWithdrawn(msg.sender, amount, token);
    }

    /**
     * @dev Get payment by ID
     */
    function getPayment(uint256 paymentId) external view returns (Payment memory) {
        require(_payments[paymentId].paymentId != 0, "CorePayroll: payment does not exist");
        return _payments[paymentId];
    }

    /**
     * @dev Get batch payment by ID
     */
    function getBatchPayment(uint256 batchId) external view returns (BatchPayment memory) {
        require(_batchPayments[batchId].batchId != 0, "CorePayroll: batch does not exist");
        return _batchPayments[batchId];
    }

    /**
     * @dev Get contract balance for a token
     */
    function getBalance(address token) external view returns (uint256) {
        if (token == address(0)) {
            return address(this).balance;
        } else {
            return IERC20(token).balanceOf(address(this));
        }
    }

    /**
     * @dev Get all payment IDs
     */
    function getAllPaymentIds() external view returns (uint256[] memory) {
        return _allPaymentIds;
    }

    /**
     * @dev Get all batch IDs
     */
    function getAllBatchIds() external view returns (uint256[] memory) {
        return _allBatchIds;
    }

    /**
     * @dev Get employee's last payment timestamp
     */
    function getEmployeeLastPayment(uint256 employeeId) external view returns (uint256) {
        return _employeeLastPayment[employeeId];
    }

    // Internal functions

    /**
     * @dev Create a payment record
     */
    function _createPayment(
        uint256 employeeId,
        address employeeWallet,
        uint256 amount,
        address token
    ) private returns (uint256) {
        uint256 paymentId = _paymentIdCounter++;

        Payment memory payment = Payment({
            paymentId: paymentId,
            employeeId: employeeId,
            employeeWallet: employeeWallet,
            amount: amount,
            token: token,
            status: PaymentStatus.Pending,
            createdAt: block.timestamp,
            completedAt: 0,
            transactionHash: bytes32(0),
            failureReason: ""
        });

        _payments[paymentId] = payment;
        _allPaymentIds.push(paymentId);

        emit PaymentCreated(paymentId, employeeId, employeeWallet, amount, token);

        return paymentId;
    }

    /**
     * @dev Execute a single payment
     */
    function _executePayment(uint256 paymentId) private {
        Payment storage payment = _payments[paymentId];
        
        payment.status = PaymentStatus.Processing;

        bool success;
        
        if (payment.token == address(0)) {
            // ETH payment
            if (address(this).balance >= payment.amount) {
                (success, ) = payment.employeeWallet.call{value: payment.amount}("");
            } else {
                success = false;
                payment.failureReason = "Insufficient ETH balance";
            }
        } else {
            // ERC20 payment
            try IERC20(payment.token).transfer(payment.employeeWallet, payment.amount) returns (bool result) {
                success = result;
            } catch Error(string memory reason) {
                success = false;
                payment.failureReason = reason;
            } catch {
                success = false;
                payment.failureReason = "Token transfer failed";
            }
        }

        if (success) {
            payment.status = PaymentStatus.Completed;
            payment.completedAt = block.timestamp;
            payment.transactionHash = blockhash(block.number - 1);
            _employeeLastPayment[payment.employeeId] = block.timestamp;
            
            emit PaymentCompleted(paymentId, payment.employeeId, payment.transactionHash);
        } else {
            payment.status = PaymentStatus.Failed;
            emit PaymentFailed(paymentId, payment.employeeId, payment.failureReason);
        }
    }

    /**
     * @dev Execute batch payments
     */
    function _executeBatchPayments(uint256 batchId) private {
        BatchPayment storage batch = _batchPayments[batchId];
        
        uint256 successCount = 0;
        uint256 failureCount = 0;

        for (uint256 i = 0; i < batch.paymentIds.length; i++) {
            _executePayment(batch.paymentIds[i]);
            
            Payment memory payment = _payments[batch.paymentIds[i]];
            
            if (payment.status == PaymentStatus.Completed) {
                successCount++;
            } else {
                failureCount++;
            }
        }

        batch.status = PaymentStatus.Completed;
        batch.completedAt = block.timestamp;
        batch.successCount = successCount;
        batch.failureCount = failureCount;

        emit BatchPaymentCompleted(batchId, successCount, failureCount);
    }

    /**
     * @dev Receive ETH
     */
    receive() external payable {
        emit FundsDeposited(msg.sender, msg.value, address(0));
    }

    /**
     * @dev Fallback function
     */
    fallback() external payable {
        emit FundsDeposited(msg.sender, msg.value, address(0));
    }
}

