// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./AccessControl.sol";

/**
 * @title EmployeeRegistry
 * @dev Smart contract for managing employee records on-chain with gas-efficient storage patterns
 */
contract EmployeeRegistry is AccessControl {
    // Enums
    enum EmployeeStatus { Active, Inactive, Suspended }
    enum PaymentFrequency { Weekly, BiWeekly, Monthly, Quarterly, Yearly }

    // Structs
    struct Employee {
        uint256 employeeId;
        address walletAddress;
        string department;
        string role;
        uint256 salaryAmount;
        address salaryToken; // address(0) for ETH
        PaymentFrequency paymentFrequency;
        EmployeeStatus status;
        uint256 createdAt;
        uint256 updatedAt;
    }

    // State variables
    uint256 private _employeeIdCounter;
    
    // Mappings
    mapping(uint256 => Employee) private _employees; // employeeId => Employee
    mapping(address => uint256) private _walletToEmployeeId; // wallet => employeeId
    mapping(string => uint256[]) private _departmentEmployees; // department => employeeIds[]
    
    uint256[] private _allEmployeeIds;

    // Events
    event EmployeeAdded(
        uint256 indexed employeeId,
        address indexed walletAddress,
        string department,
        string role,
        uint256 salaryAmount,
        address salaryToken
    );
    
    event EmployeeUpdated(
        uint256 indexed employeeId,
        address indexed walletAddress,
        string department,
        string role,
        uint256 salaryAmount,
        address salaryToken
    );
    
    event EmployeeRemoved(uint256 indexed employeeId, address indexed walletAddress);
    
    event EmployeeStatusChanged(
        uint256 indexed employeeId,
        address indexed walletAddress,
        EmployeeStatus oldStatus,
        EmployeeStatus newStatus
    );

    /**
     * @dev Constructor - inherits from AccessControl
     */
    constructor() AccessControl() {
        _employeeIdCounter = 1;
    }

    /**
     * @dev Add a new employee to the registry
     * @param walletAddress Employee's wallet address
     * @param department Employee's department
     * @param role Employee's role
     * @param salaryAmount Employee's salary amount
     * @param salaryToken Token address for salary (address(0) for ETH)
     * @param paymentFrequency How often the employee is paid
     */
    function addEmployee(
        address walletAddress,
        string calldata department,
        string calldata role,
        uint256 salaryAmount,
        address salaryToken,
        PaymentFrequency paymentFrequency
    ) external onlyRole(ADMIN_ROLE) returns (uint256) {
        require(walletAddress != address(0), "EmployeeRegistry: wallet address cannot be zero");
        require(_walletToEmployeeId[walletAddress] == 0, "EmployeeRegistry: wallet already registered");
        require(salaryAmount > 0, "EmployeeRegistry: salary amount must be greater than zero");
        require(bytes(department).length > 0, "EmployeeRegistry: department cannot be empty");
        require(bytes(role).length > 0, "EmployeeRegistry: role cannot be empty");

        uint256 employeeId = _employeeIdCounter++;
        
        Employee memory newEmployee = Employee({
            employeeId: employeeId,
            walletAddress: walletAddress,
            department: department,
            role: role,
            salaryAmount: salaryAmount,
            salaryToken: salaryToken,
            paymentFrequency: paymentFrequency,
            status: EmployeeStatus.Active,
            createdAt: block.timestamp,
            updatedAt: block.timestamp
        });

        _employees[employeeId] = newEmployee;
        _walletToEmployeeId[walletAddress] = employeeId;
        _departmentEmployees[department].push(employeeId);
        _allEmployeeIds.push(employeeId);

        // Grant employee role to the wallet
        grantRole(EMPLOYEE_ROLE, walletAddress);

        emit EmployeeAdded(employeeId, walletAddress, department, role, salaryAmount, salaryToken);

        return employeeId;
    }

    /**
     * @dev Update an existing employee's information
     */
    function updateEmployee(
        uint256 employeeId,
        string calldata department,
        string calldata role,
        uint256 salaryAmount,
        address salaryToken,
        PaymentFrequency paymentFrequency
    ) external onlyRole(ADMIN_ROLE) {
        require(_employees[employeeId].employeeId != 0, "EmployeeRegistry: employee does not exist");
        require(salaryAmount > 0, "EmployeeRegistry: salary amount must be greater than zero");
        require(bytes(department).length > 0, "EmployeeRegistry: department cannot be empty");
        require(bytes(role).length > 0, "EmployeeRegistry: role cannot be empty");

        Employee storage employee = _employees[employeeId];
        
        // Update department mapping if department changed
        if (keccak256(bytes(employee.department)) != keccak256(bytes(department))) {
            _removeDepartmentEmployee(employee.department, employeeId);
            _departmentEmployees[department].push(employeeId);
        }

        employee.department = department;
        employee.role = role;
        employee.salaryAmount = salaryAmount;
        employee.salaryToken = salaryToken;
        employee.paymentFrequency = paymentFrequency;
        employee.updatedAt = block.timestamp;

        emit EmployeeUpdated(employeeId, employee.walletAddress, department, role, salaryAmount, salaryToken);
    }

    /**
     * @dev Remove an employee from the registry
     */
    function removeEmployee(uint256 employeeId) external onlyRole(ADMIN_ROLE) {
        require(_employees[employeeId].employeeId != 0, "EmployeeRegistry: employee does not exist");

        Employee memory employee = _employees[employeeId];
        
        // Revoke employee role
        revokeRole(EMPLOYEE_ROLE, employee.walletAddress);
        
        // Clean up mappings
        delete _walletToEmployeeId[employee.walletAddress];
        _removeDepartmentEmployee(employee.department, employeeId);
        _removeFromAllEmployees(employeeId);
        delete _employees[employeeId];

        emit EmployeeRemoved(employeeId, employee.walletAddress);
    }

    /**
     * @dev Update employee status (Active, Inactive, Suspended)
     */
    function updateEmployeeStatus(uint256 employeeId, EmployeeStatus newStatus) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        require(_employees[employeeId].employeeId != 0, "EmployeeRegistry: employee does not exist");

        Employee storage employee = _employees[employeeId];
        EmployeeStatus oldStatus = employee.status;
        
        require(oldStatus != newStatus, "EmployeeRegistry: status is already set to this value");

        employee.status = newStatus;
        employee.updatedAt = block.timestamp;

        emit EmployeeStatusChanged(employeeId, employee.walletAddress, oldStatus, newStatus);
    }

    /**
     * @dev Get employee information by ID
     */
    function getEmployee(uint256 employeeId) external view returns (Employee memory) {
        require(_employees[employeeId].employeeId != 0, "EmployeeRegistry: employee does not exist");
        return _employees[employeeId];
    }

    /**
     * @dev Get employee ID by wallet address
     */
    function getEmployeeIdByWallet(address walletAddress) external view returns (uint256) {
        uint256 employeeId = _walletToEmployeeId[walletAddress];
        require(employeeId != 0, "EmployeeRegistry: wallet not registered");
        return employeeId;
    }

    /**
     * @dev Get all employees in a department
     */
    function getEmployeesByDepartment(string calldata department) 
        external 
        view 
        returns (uint256[] memory) 
    {
        return _departmentEmployees[department];
    }

    /**
     * @dev Get all employee IDs
     */
    function getAllEmployeeIds() external view returns (uint256[] memory) {
        return _allEmployeeIds;
    }

    /**
     * @dev Get all active employees
     */
    function getActiveEmployees() external view returns (uint256[] memory) {
        uint256 activeCount = 0;
        
        // Count active employees
        for (uint256 i = 0; i < _allEmployeeIds.length; i++) {
            if (_employees[_allEmployeeIds[i]].status == EmployeeStatus.Active) {
                activeCount++;
            }
        }

        // Create array of active employee IDs
        uint256[] memory activeEmployees = new uint256[](activeCount);
        uint256 index = 0;
        
        for (uint256 i = 0; i < _allEmployeeIds.length; i++) {
            if (_employees[_allEmployeeIds[i]].status == EmployeeStatus.Active) {
                activeEmployees[index] = _allEmployeeIds[i];
                index++;
            }
        }

        return activeEmployees;
    }

    /**
     * @dev Check if an employee exists
     */
    function employeeExists(uint256 employeeId) external view returns (bool) {
        return _employees[employeeId].employeeId != 0;
    }

    /**
     * @dev Check if a wallet is registered
     */
    function isWalletRegistered(address walletAddress) external view returns (bool) {
        return _walletToEmployeeId[walletAddress] != 0;
    }

    /**
     * @dev Get total number of employees
     */
    function getTotalEmployees() external view returns (uint256) {
        return _allEmployeeIds.length;
    }

    // Internal helper functions

    /**
     * @dev Remove employee from department array
     */
    function _removeDepartmentEmployee(string memory department, uint256 employeeId) private {
        uint256[] storage employees = _departmentEmployees[department];
        for (uint256 i = 0; i < employees.length; i++) {
            if (employees[i] == employeeId) {
                employees[i] = employees[employees.length - 1];
                employees.pop();
                break;
            }
        }
    }

    /**
     * @dev Remove employee from all employees array
     */
    function _removeFromAllEmployees(uint256 employeeId) private {
        for (uint256 i = 0; i < _allEmployeeIds.length; i++) {
            if (_allEmployeeIds[i] == employeeId) {
                _allEmployeeIds[i] = _allEmployeeIds[_allEmployeeIds.length - 1];
                _allEmployeeIds.pop();
                break;
            }
        }
    }
}

