// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./AccessControl.sol";

/**
 * @title InvoiceManager
 * @dev Smart contract for generating and storing immutable payment receipts and invoices
 */
contract InvoiceManager is AccessControl {
    // Enums
    enum InvoiceStatus { Draft, Pending, Paid, Cancelled }

    // Structs
    struct Invoice {
        uint256 invoiceId;
        uint256 employeeId;
        address employeeWallet;
        uint256 amount;
        address token; // address(0) for ETH
        InvoiceStatus status;
        uint256 createdAt;
        uint256 dueDate;
        uint256 paidAt;
        bytes32 transactionHash; // Hash of the payment transaction
        string description;
    }

    struct Receipt {
        uint256 receiptId;
        uint256 invoiceId;
        uint256 employeeId;
        address employeeWallet;
        uint256 amount;
        address token;
        uint256 paidAt;
        bytes32 transactionHash;
        address paidBy;
    }

    // State variables
    uint256 private _invoiceIdCounter;
    uint256 private _receiptIdCounter;

    // Mappings
    mapping(uint256 => Invoice) private _invoices; // invoiceId => Invoice
    mapping(uint256 => Receipt) private _receipts; // receiptId => Receipt
    mapping(uint256 => uint256[]) private _employeeInvoices; // employeeId => invoiceIds[]
    mapping(uint256 => uint256[]) private _employeeReceipts; // employeeId => receiptIds[]
    mapping(bytes32 => uint256) private _txHashToReceipt; // transactionHash => receiptId
    
    uint256[] private _allInvoiceIds;
    uint256[] private _allReceiptIds;

    // Events
    event InvoiceCreated(
        uint256 indexed invoiceId,
        uint256 indexed employeeId,
        address indexed employeeWallet,
        uint256 amount,
        address token,
        uint256 dueDate
    );

    event InvoiceStatusChanged(
        uint256 indexed invoiceId,
        InvoiceStatus oldStatus,
        InvoiceStatus newStatus
    );

    event InvoicePaid(
        uint256 indexed invoiceId,
        uint256 indexed receiptId,
        bytes32 transactionHash,
        uint256 paidAt
    );

    event ReceiptGenerated(
        uint256 indexed receiptId,
        uint256 indexed invoiceId,
        uint256 indexed employeeId,
        bytes32 transactionHash
    );

    event InvoiceCancelled(uint256 indexed invoiceId);

    /**
     * @dev Constructor - inherits from AccessControl
     */
    constructor() AccessControl() {
        _invoiceIdCounter = 1;
        _receiptIdCounter = 1;
    }

    /**
     * @dev Create a new invoice
     */
    function createInvoice(
        uint256 employeeId,
        address employeeWallet,
        uint256 amount,
        address token,
        uint256 dueDate,
        string calldata description
    ) external onlyRole(FINANCE_MANAGER_ROLE) returns (uint256) {
        require(employeeWallet != address(0), "InvoiceManager: invalid employee wallet");
        require(amount > 0, "InvoiceManager: amount must be greater than zero");
        require(dueDate > block.timestamp, "InvoiceManager: due date must be in the future");

        uint256 invoiceId = _invoiceIdCounter++;

        Invoice memory newInvoice = Invoice({
            invoiceId: invoiceId,
            employeeId: employeeId,
            employeeWallet: employeeWallet,
            amount: amount,
            token: token,
            status: InvoiceStatus.Draft,
            createdAt: block.timestamp,
            dueDate: dueDate,
            paidAt: 0,
            transactionHash: bytes32(0),
            description: description
        });

        _invoices[invoiceId] = newInvoice;
        _employeeInvoices[employeeId].push(invoiceId);
        _allInvoiceIds.push(invoiceId);

        emit InvoiceCreated(invoiceId, employeeId, employeeWallet, amount, token, dueDate);

        return invoiceId;
    }

    /**
     * @dev Update invoice status
     */
    function updateInvoiceStatus(uint256 invoiceId, InvoiceStatus newStatus) 
        external 
        onlyRole(FINANCE_MANAGER_ROLE) 
    {
        require(_invoices[invoiceId].invoiceId != 0, "InvoiceManager: invoice does not exist");
        
        Invoice storage invoice = _invoices[invoiceId];
        InvoiceStatus oldStatus = invoice.status;
        
        require(oldStatus != InvoiceStatus.Paid, "InvoiceManager: cannot change status of paid invoice");
        require(oldStatus != InvoiceStatus.Cancelled, "InvoiceManager: cannot change status of cancelled invoice");
        require(oldStatus != newStatus, "InvoiceManager: status is already set to this value");

        invoice.status = newStatus;

        emit InvoiceStatusChanged(invoiceId, oldStatus, newStatus);
    }

    /**
     * @dev Mark invoice as paid and generate receipt
     * @param invoiceId The invoice ID
     * @param transactionHash The blockchain transaction hash
     */
    function markInvoiceAsPaid(uint256 invoiceId, bytes32 transactionHash) 
        external 
        returns (uint256) 
    {
        require(
            hasRole(FINANCE_MANAGER_ROLE, msg.sender) || hasRole(ADMIN_ROLE, msg.sender),
            "InvoiceManager: must be finance manager or admin"
        );
        require(_invoices[invoiceId].invoiceId != 0, "InvoiceManager: invoice does not exist");
        require(transactionHash != bytes32(0), "InvoiceManager: invalid transaction hash");

        Invoice storage invoice = _invoices[invoiceId];
        
        require(invoice.status == InvoiceStatus.Pending, "InvoiceManager: invoice must be in pending status");

        // Update invoice
        invoice.status = InvoiceStatus.Paid;
        invoice.paidAt = block.timestamp;
        invoice.transactionHash = transactionHash;

        // Generate receipt
        uint256 receiptId = _generateReceipt(invoice, transactionHash);

        emit InvoicePaid(invoiceId, receiptId, transactionHash, block.timestamp);

        return receiptId;
    }

    /**
     * @dev Cancel an invoice
     */
    function cancelInvoice(uint256 invoiceId) external onlyRole(FINANCE_MANAGER_ROLE) {
        require(_invoices[invoiceId].invoiceId != 0, "InvoiceManager: invoice does not exist");
        
        Invoice storage invoice = _invoices[invoiceId];
        
        require(invoice.status != InvoiceStatus.Paid, "InvoiceManager: cannot cancel paid invoice");
        require(invoice.status != InvoiceStatus.Cancelled, "InvoiceManager: invoice already cancelled");

        InvoiceStatus oldStatus = invoice.status;
        invoice.status = InvoiceStatus.Cancelled;

        emit InvoiceStatusChanged(invoiceId, oldStatus, InvoiceStatus.Cancelled);
        emit InvoiceCancelled(invoiceId);
    }

    /**
     * @dev Get invoice by ID
     */
    function getInvoice(uint256 invoiceId) external view returns (Invoice memory) {
        require(_invoices[invoiceId].invoiceId != 0, "InvoiceManager: invoice does not exist");
        return _invoices[invoiceId];
    }

    /**
     * @dev Get receipt by ID
     */
    function getReceipt(uint256 receiptId) external view returns (Receipt memory) {
        require(_receipts[receiptId].receiptId != 0, "InvoiceManager: receipt does not exist");
        return _receipts[receiptId];
    }

    /**
     * @dev Get receipt by transaction hash
     */
    function getReceiptByTxHash(bytes32 transactionHash) external view returns (Receipt memory) {
        uint256 receiptId = _txHashToReceipt[transactionHash];
        require(receiptId != 0, "InvoiceManager: receipt not found for transaction hash");
        return _receipts[receiptId];
    }

    /**
     * @dev Get all invoices for an employee
     */
    function getEmployeeInvoices(uint256 employeeId) external view returns (uint256[] memory) {
        return _employeeInvoices[employeeId];
    }

    /**
     * @dev Get all receipts for an employee
     */
    function getEmployeeReceipts(uint256 employeeId) external view returns (uint256[] memory) {
        return _employeeReceipts[employeeId];
    }

    /**
     * @dev Get invoices by status
     */
    function getInvoicesByStatus(InvoiceStatus status) external view returns (uint256[] memory) {
        uint256 count = 0;
        
        // Count invoices with matching status
        for (uint256 i = 0; i < _allInvoiceIds.length; i++) {
            if (_invoices[_allInvoiceIds[i]].status == status) {
                count++;
            }
        }

        // Create array of matching invoice IDs
        uint256[] memory matchingInvoices = new uint256[](count);
        uint256 index = 0;
        
        for (uint256 i = 0; i < _allInvoiceIds.length; i++) {
            if (_invoices[_allInvoiceIds[i]].status == status) {
                matchingInvoices[index] = _allInvoiceIds[i];
                index++;
            }
        }

        return matchingInvoices;
    }

    /**
     * @dev Get invoices by date range
     */
    function getInvoicesByDateRange(uint256 startDate, uint256 endDate) 
        external 
        view 
        returns (uint256[] memory) 
    {
        require(startDate <= endDate, "InvoiceManager: invalid date range");

        uint256 count = 0;
        
        // Count invoices in date range
        for (uint256 i = 0; i < _allInvoiceIds.length; i++) {
            uint256 createdAt = _invoices[_allInvoiceIds[i]].createdAt;
            if (createdAt >= startDate && createdAt <= endDate) {
                count++;
            }
        }

        // Create array of matching invoice IDs
        uint256[] memory matchingInvoices = new uint256[](count);
        uint256 index = 0;
        
        for (uint256 i = 0; i < _allInvoiceIds.length; i++) {
            uint256 createdAt = _invoices[_allInvoiceIds[i]].createdAt;
            if (createdAt >= startDate && createdAt <= endDate) {
                matchingInvoices[index] = _allInvoiceIds[i];
                index++;
            }
        }

        return matchingInvoices;
    }

    /**
     * @dev Get receipts by date range
     */
    function getReceiptsByDateRange(uint256 startDate, uint256 endDate) 
        external 
        view 
        returns (uint256[] memory) 
    {
        require(startDate <= endDate, "InvoiceManager: invalid date range");

        uint256 count = 0;
        
        // Count receipts in date range
        for (uint256 i = 0; i < _allReceiptIds.length; i++) {
            uint256 paidAt = _receipts[_allReceiptIds[i]].paidAt;
            if (paidAt >= startDate && paidAt <= endDate) {
                count++;
            }
        }

        // Create array of matching receipt IDs
        uint256[] memory matchingReceipts = new uint256[](count);
        uint256 index = 0;
        
        for (uint256 i = 0; i < _allReceiptIds.length; i++) {
            uint256 paidAt = _receipts[_allReceiptIds[i]].paidAt;
            if (paidAt >= startDate && paidAt <= endDate) {
                matchingReceipts[index] = _allReceiptIds[i];
                index++;
            }
        }

        return matchingReceipts;
    }

    /**
     * @dev Get all invoice IDs
     */
    function getAllInvoiceIds() external view returns (uint256[] memory) {
        return _allInvoiceIds;
    }

    /**
     * @dev Get all receipt IDs
     */
    function getAllReceiptIds() external view returns (uint256[] memory) {
        return _allReceiptIds;
    }

    /**
     * @dev Get total invoices count
     */
    function getTotalInvoices() external view returns (uint256) {
        return _allInvoiceIds.length;
    }

    /**
     * @dev Get total receipts count
     */
    function getTotalReceipts() external view returns (uint256) {
        return _allReceiptIds.length;
    }

    // Internal functions

    /**
     * @dev Internal function to generate a receipt
     */
    function _generateReceipt(Invoice memory invoice, bytes32 transactionHash) 
        private 
        returns (uint256) 
    {
        uint256 receiptId = _receiptIdCounter++;

        Receipt memory newReceipt = Receipt({
            receiptId: receiptId,
            invoiceId: invoice.invoiceId,
            employeeId: invoice.employeeId,
            employeeWallet: invoice.employeeWallet,
            amount: invoice.amount,
            token: invoice.token,
            paidAt: block.timestamp,
            transactionHash: transactionHash,
            paidBy: msg.sender
        });

        _receipts[receiptId] = newReceipt;
        _employeeReceipts[invoice.employeeId].push(receiptId);
        _txHashToReceipt[transactionHash] = receiptId;
        _allReceiptIds.push(receiptId);

        emit ReceiptGenerated(receiptId, invoice.invoiceId, invoice.employeeId, transactionHash);

        return receiptId;
    }
}

