// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./AccessControl.sol";

/**
 * @title PaymentApproval
 * @dev Smart contract for payment approval workflow with audit trail
 */
contract PaymentApproval is AccessControl {
    // Enums
    enum ApprovalStatus { Pending, Approved, Rejected, Cancelled, Executed }

    // Structs
    struct PaymentRequest {
        uint256 requestId;
        uint256[] employeeIds;
        address[] employeeWallets;
        uint256[] amounts;
        address token; // address(0) for ETH
        ApprovalStatus status;
        uint256 createdAt;
        uint256 approvedAt;
        uint256 executedAt;
        uint256 autoApprovalTime; // Timestamp for automatic approval (0 if disabled)
        address requestedBy;
        address approvedBy;
        string reason;
    }

    struct ApprovalRecord {
        uint256 recordId;
        uint256 requestId;
        address approver;
        ApprovalStatus decision;
        uint256 timestamp;
        string comments;
    }

    // State variables
    uint256 private _requestIdCounter;
    uint256 private _recordIdCounter;
    uint256 public autoApprovalDelay; // Default delay in seconds for auto-approval (0 = disabled)

    // Mappings
    mapping(uint256 => PaymentRequest) private _paymentRequests;
    mapping(uint256 => ApprovalRecord[]) private _approvalHistory; // requestId => ApprovalRecord[]
    mapping(uint256 => bool) private _requestExists;
    
    uint256[] private _allRequestIds;
    uint256[] private _pendingRequestIds;

    // Events
    event PaymentRequestCreated(
        uint256 indexed requestId,
        address indexed requestedBy,
        uint256 employeeCount,
        uint256 totalAmount,
        address token
    );

    event PaymentRequestApproved(
        uint256 indexed requestId,
        address indexed approver,
        uint256 timestamp
    );

    event PaymentRequestRejected(
        uint256 indexed requestId,
        address indexed approver,
        string reason,
        uint256 timestamp
    );

    event PaymentRequestCancelled(
        uint256 indexed requestId,
        address indexed cancelledBy,
        uint256 timestamp
    );

    event PaymentRequestExecuted(
        uint256 indexed requestId,
        uint256 timestamp
    );

    event ApprovalRecordAdded(
        uint256 indexed recordId,
        uint256 indexed requestId,
        address indexed approver,
        ApprovalStatus decision
    );

    event AutoApprovalDelayUpdated(uint256 oldDelay, uint256 newDelay);

    /**
     * @dev Constructor - inherits from AccessControl
     */
    constructor(uint256 _autoApprovalDelay) AccessControl() {
        _requestIdCounter = 1;
        _recordIdCounter = 1;
        autoApprovalDelay = _autoApprovalDelay;
    }

    /**
     * @dev Create a new payment request
     */
    function createPaymentRequest(
        uint256[] calldata employeeIds,
        address[] calldata employeeWallets,
        uint256[] calldata amounts,
        address token,
        string calldata reason
    ) external returns (uint256) {
        require(
            hasRole(FINANCE_MANAGER_ROLE, msg.sender) || hasRole(ADMIN_ROLE, msg.sender),
            "PaymentApproval: must be finance manager or admin"
        );
        require(employeeIds.length > 0, "PaymentApproval: no employees specified");
        require(
            employeeIds.length == employeeWallets.length && 
            employeeIds.length == amounts.length,
            "PaymentApproval: array length mismatch"
        );

        // Calculate total amount
        uint256 totalAmount = 0;
        for (uint256 i = 0; i < amounts.length; i++) {
            require(amounts[i] > 0, "PaymentApproval: amount must be greater than zero");
            require(employeeWallets[i] != address(0), "PaymentApproval: invalid employee wallet");
            totalAmount += amounts[i];
        }

        uint256 requestId = _requestIdCounter++;
        
        // Set auto-approval time if enabled
        uint256 autoApprovalTime = 0;
        if (autoApprovalDelay > 0) {
            autoApprovalTime = block.timestamp + autoApprovalDelay;
        }

        PaymentRequest memory newRequest = PaymentRequest({
            requestId: requestId,
            employeeIds: employeeIds,
            employeeWallets: employeeWallets,
            amounts: amounts,
            token: token,
            status: ApprovalStatus.Pending,
            createdAt: block.timestamp,
            approvedAt: 0,
            executedAt: 0,
            autoApprovalTime: autoApprovalTime,
            requestedBy: msg.sender,
            approvedBy: address(0),
            reason: reason
        });

        _paymentRequests[requestId] = newRequest;
        _requestExists[requestId] = true;
        _allRequestIds.push(requestId);
        _pendingRequestIds.push(requestId);

        emit PaymentRequestCreated(requestId, msg.sender, employeeIds.length, totalAmount, token);

        return requestId;
    }

    /**
     * @dev Approve a payment request
     */
    function approvePaymentRequest(uint256 requestId, string calldata comments) 
        external 
    {
        require(
            hasRole(ADMIN_ROLE, msg.sender) || hasRole(OWNER_ROLE, msg.sender),
            "PaymentApproval: must be admin or owner to approve"
        );
        require(_requestExists[requestId], "PaymentApproval: request does not exist");

        PaymentRequest storage request = _paymentRequests[requestId];
        
        require(request.status == ApprovalStatus.Pending, "PaymentApproval: request is not pending");
        require(request.requestedBy != msg.sender, "PaymentApproval: cannot approve own request");

        // Update request
        request.status = ApprovalStatus.Approved;
        request.approvedAt = block.timestamp;
        request.approvedBy = msg.sender;

        // Add approval record
        _addApprovalRecord(requestId, msg.sender, ApprovalStatus.Approved, comments);

        // Remove from pending list
        _removeFromPendingList(requestId);

        emit PaymentRequestApproved(requestId, msg.sender, block.timestamp);
    }

    /**
     * @dev Reject a payment request
     */
    function rejectPaymentRequest(uint256 requestId, string calldata comments) 
        external 
    {
        require(
            hasRole(ADMIN_ROLE, msg.sender) || hasRole(OWNER_ROLE, msg.sender),
            "PaymentApproval: must be admin or owner to reject"
        );
        require(_requestExists[requestId], "PaymentApproval: request does not exist");

        PaymentRequest storage request = _paymentRequests[requestId];
        
        require(request.status == ApprovalStatus.Pending, "PaymentApproval: request is not pending");

        // Update request
        request.status = ApprovalStatus.Rejected;

        // Add approval record
        _addApprovalRecord(requestId, msg.sender, ApprovalStatus.Rejected, comments);

        // Remove from pending list
        _removeFromPendingList(requestId);

        emit PaymentRequestRejected(requestId, msg.sender, comments, block.timestamp);
    }

    /**
     * @dev Cancel a pending payment request
     */
    function cancelPaymentRequest(uint256 requestId, string calldata comments) 
        external 
    {
        require(_requestExists[requestId], "PaymentApproval: request does not exist");

        PaymentRequest storage request = _paymentRequests[requestId];
        
        require(
            request.requestedBy == msg.sender || 
            hasRole(ADMIN_ROLE, msg.sender) || 
            hasRole(OWNER_ROLE, msg.sender),
            "PaymentApproval: not authorized to cancel"
        );
        require(
            request.status == ApprovalStatus.Pending || request.status == ApprovalStatus.Approved,
            "PaymentApproval: can only cancel pending or approved requests"
        );

        // Update request
        request.status = ApprovalStatus.Cancelled;

        // Add approval record
        _addApprovalRecord(requestId, msg.sender, ApprovalStatus.Cancelled, comments);

        // Remove from pending list if applicable
        if (request.status == ApprovalStatus.Pending) {
            _removeFromPendingList(requestId);
        }

        emit PaymentRequestCancelled(requestId, msg.sender, block.timestamp);
    }

    /**
     * @dev Mark a payment request as executed (called by CorePayroll contract)
     */
    function markAsExecuted(uint256 requestId) external {
        require(
            hasRole(ADMIN_ROLE, msg.sender) || hasRole(FINANCE_MANAGER_ROLE, msg.sender),
            "PaymentApproval: not authorized to mark as executed"
        );
        require(_requestExists[requestId], "PaymentApproval: request does not exist");

        PaymentRequest storage request = _paymentRequests[requestId];
        
        require(
            request.status == ApprovalStatus.Approved,
            "PaymentApproval: request must be approved"
        );

        request.status = ApprovalStatus.Executed;
        request.executedAt = block.timestamp;

        emit PaymentRequestExecuted(requestId, block.timestamp);
    }

    /**
     * @dev Check if a request can be auto-approved
     */
    function canAutoApprove(uint256 requestId) public view returns (bool) {
        require(_requestExists[requestId], "PaymentApproval: request does not exist");

        PaymentRequest memory request = _paymentRequests[requestId];
        
        return request.status == ApprovalStatus.Pending &&
               request.autoApprovalTime > 0 &&
               block.timestamp >= request.autoApprovalTime;
    }

    /**
     * @dev Auto-approve a request if conditions are met
     */
    function autoApprove(uint256 requestId) external {
        require(canAutoApprove(requestId), "PaymentApproval: cannot auto-approve");

        PaymentRequest storage request = _paymentRequests[requestId];
        
        request.status = ApprovalStatus.Approved;
        request.approvedAt = block.timestamp;
        request.approvedBy = address(this); // Contract address as approver

        // Add approval record
        _addApprovalRecord(requestId, address(this), ApprovalStatus.Approved, "Auto-approved");

        // Remove from pending list
        _removeFromPendingList(requestId);

        emit PaymentRequestApproved(requestId, address(this), block.timestamp);
    }

    /**
     * @dev Update auto-approval delay
     */
    function setAutoApprovalDelay(uint256 newDelay) external onlyOwner {
        uint256 oldDelay = autoApprovalDelay;
        autoApprovalDelay = newDelay;
        emit AutoApprovalDelayUpdated(oldDelay, newDelay);
    }

    /**
     * @dev Get payment request by ID
     */
    function getPaymentRequest(uint256 requestId) external view returns (PaymentRequest memory) {
        require(_requestExists[requestId], "PaymentApproval: request does not exist");
        return _paymentRequests[requestId];
    }

    /**
     * @dev Get approval history for a request
     */
    function getApprovalHistory(uint256 requestId) 
        external 
        view 
        returns (ApprovalRecord[] memory) 
    {
        require(_requestExists[requestId], "PaymentApproval: request does not exist");
        return _approvalHistory[requestId];
    }

    /**
     * @dev Get all pending requests
     */
    function getPendingRequests() external view returns (uint256[] memory) {
        return _pendingRequestIds;
    }

    /**
     * @dev Get all request IDs
     */
    function getAllRequestIds() external view returns (uint256[] memory) {
        return _allRequestIds;
    }

    /**
     * @dev Get requests by status
     */
    function getRequestsByStatus(ApprovalStatus status) 
        external 
        view 
        returns (uint256[] memory) 
    {
        uint256 count = 0;
        
        // Count requests with matching status
        for (uint256 i = 0; i < _allRequestIds.length; i++) {
            if (_paymentRequests[_allRequestIds[i]].status == status) {
                count++;
            }
        }

        // Create array of matching request IDs
        uint256[] memory matchingRequests = new uint256[](count);
        uint256 index = 0;
        
        for (uint256 i = 0; i < _allRequestIds.length; i++) {
            if (_paymentRequests[_allRequestIds[i]].status == status) {
                matchingRequests[index] = _allRequestIds[i];
                index++;
            }
        }

        return matchingRequests;
    }

    /**
     * @dev Get total requests count
     */
    function getTotalRequests() external view returns (uint256) {
        return _allRequestIds.length;
    }

    // Internal functions

    /**
     * @dev Add an approval record to history
     */
    function _addApprovalRecord(
        uint256 requestId,
        address approver,
        ApprovalStatus decision,
        string memory comments
    ) private {
        uint256 recordId = _recordIdCounter++;

        ApprovalRecord memory record = ApprovalRecord({
            recordId: recordId,
            requestId: requestId,
            approver: approver,
            decision: decision,
            timestamp: block.timestamp,
            comments: comments
        });

        _approvalHistory[requestId].push(record);

        emit ApprovalRecordAdded(recordId, requestId, approver, decision);
    }

    /**
     * @dev Remove request from pending list
     */
    function _removeFromPendingList(uint256 requestId) private {
        for (uint256 i = 0; i < _pendingRequestIds.length; i++) {
            if (_pendingRequestIds[i] == requestId) {
                _pendingRequestIds[i] = _pendingRequestIds[_pendingRequestIds.length - 1];
                _pendingRequestIds.pop();
                break;
            }
        }
    }
}

